mapper.select:
	
		
			select
				FAQ_NUM, FAQ_CATEGORY, SUBJECT, CONTENT
			from(
				select * from
                
				(
				select
			 		rownum rn, FAQ_NUM, FAQ_CATEGORY, SUBJECT, CONTENT 
				from SERVICE_FAQ
				where
					FAQ_CATEGORY = '변경/취소' 
				)
					
			
 			
			
		
					where rownum <= #{pageNum} * #{amount}
					ORDER BY FAQ_NUM desc
				)
			where rn > (#{pageNum} -1) * #{amount}

	
		
			select
				FAQ_NUM, FAQ_CATEGORY, SUBJECT, CONTENT
			from(
				select * from
                
				(
				select
			 		rownum rn, FAQ_NUM, FAQ_CATEGORY, SUBJECT, CONTENT 
				from SERVICE_FAQ
				where
					FAQ_CATEGORY = '교환/반품' 
				)
					
			
 			
			
		
					where rownum <= #{pageNum} * #{amount}
					ORDER BY FAQ_NUM desc
				)
			where rn > (#{pageNum} -1) * #{amount}

	
		
			select
				FAQ_NUM, FAQ_CATEGORY, SUBJECT, CONTENT
			from(
				select * from
                
				(
				select
			 		rownum rn, FAQ_NUM, FAQ_CATEGORY, SUBJECT, CONTENT 
				from SERVICE_FAQ
				where
					FAQ_CATEGORY = '주문/조회' 
				)
					
			
 			
			
		
					where rownum <= #{pageNum} * #{amount}
					ORDER BY FAQ_NUM desc
				)
			where rn > (#{pageNum} -1) * #{amount}

	
		
			select
				FAQ_NUM, FAQ_CATEGORY, SUBJECT, CONTENT
			from(
				select * from
                
				(
				select
			 		rownum rn, FAQ_NUM, FAQ_CATEGORY, SUBJECT, CONTENT 
				from SERVICE_FAQ
				where
					FAQ_CATEGORY = '상품문의' 
				)
					
			
 			
			
		
					where rownum <= #{pageNum} * #{amount}
					ORDER BY FAQ_NUM desc
				)
			where rn > (#{pageNum} -1) * #{amount}
